async function s(){const e=document.createElement("p");try{await(await fetch("https://newsletter.ianwootten.co.uk/members/api/send-magic-link/",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({email:this.email,emailType:"subscribe"})})).text()=="Created."?e.innerHTML="Thanks! Go check your email to complete signup.":e.innerHTML="Whoops! Something went wrong - you can still subscribe using the link in the header"}catch(n){console.log(n)}const t=document.getElementById("ghost-form");t.parentNode.replaceChild(e,t)}document.getElementById("subscribe_submit").addEventListener("click",s);
